const t=JSON.parse('{"key":"v-0fd9f066","path":"/lc/algorithms/graph/articulation-points/","title":"关节点（或割点）","lang":"zh-CN","frontmatter":{"description":"在一个无向连通图中，如果移除一个顶点（以及通过该顶点的边），会导致图断开，则该顶点被称为关节点（或割点）。关节点代表了连通网络中的脆弱点，即单个点的故障会将网络分割成两个或更多个不连通的部分。它们在设计可靠的网络时非常有用。 对于一个不连通的无向图，关节点是指移除该顶点后，连通分量的数量增加。 参考资料 GeeksForGeeks (https://w...","head":[["meta",{"property":"og:url","content":"https://www.coding-time.cn/lc/algorithms/graph/articulation-points/"}],["meta",{"property":"og:site_name","content":"编程时光"}],["meta",{"property":"og:title","content":"关节点（或割点）"}],["meta",{"property":"og:description","content":"在一个无向连通图中，如果移除一个顶点（以及通过该顶点的边），会导致图断开，则该顶点被称为关节点（或割点）。关节点代表了连通网络中的脆弱点，即单个点的故障会将网络分割成两个或更多个不连通的部分。它们在设计可靠的网络时非常有用。 对于一个不连通的无向图，关节点是指移除该顶点后，连通分量的数量增加。 参考资料 GeeksForGeeks (https://w..."}],["meta",{"property":"og:type","content":"article"}],["meta",{"property":"og:locale","content":"zh-CN"}],["meta",{"property":"og:updated_time","content":"2023-07-14T16:27:54.000Z"}],["meta",{"property":"article:author","content":"linwu"}],["meta",{"property":"article:modified_time","content":"2023-07-14T16:27:54.000Z"}],["script",{"type":"application/ld+json"},"{\\"@context\\":\\"https://schema.org\\",\\"@type\\":\\"Article\\",\\"headline\\":\\"关节点（或割点）\\",\\"image\\":[\\"\\"],\\"dateModified\\":\\"2023-07-14T16:27:54.000Z\\",\\"author\\":[{\\"@type\\":\\"Person\\",\\"name\\":\\"linwu\\",\\"url\\":\\"https://www.coding-time.cn\\",\\"email\\":\\"linwu.hi@gmail.com\\"}]}"]]},"headers":[{"level":2,"title":"参考资料","slug":"参考资料","link":"#参考资料","children":[]}],"git":{"createdTime":1689352074000,"updatedTime":1689352074000,"contributors":[{"name":"linwu-hi","email":"linwu.hi@gmail.com","commits":1}]},"readingTime":{"minutes":0.53,"words":160},"filePathRelative":"lc/algorithms/graph/articulation-points/README.md","localizedDate":"2023年7月15日","autoDesc":true}');export{t as data};
